

function checkImgMobile() {
    let baiVietElement = document.getElementById('bai-viet');
    if (!baiVietElement || window.innerWidth > 900) return;
    let elements = document.querySelectorAll('.glow-on-hover');
    let imgElements = baiVietElement.getElementsByTagName('img');
    let h2Elements = baiVietElement.getElementsByTagName('h2');
    let h3Elements = baiVietElement.getElementsByTagName('h3');
    if (elements) { elements.forEach(element => { element.classList.add('glow-on-hover') }) }
    Array.from(imgElements).forEach(img => {
        const src = img.getAttribute('src');
        if (src && !src.startsWith('/ckfinder/userfiles/images/Icon')) {
            img.classList.add('img-responsive')
        }
        // if (img.src.startsWith('https://nk.phongkhamandonghcm.com/ckfinder/userfiles/images/Chat/Chat-Dakhoa.gif')) {
        if (img.src.startsWith('http://localhost/ckfinder/userfiles/images/Chat/Chat-Dakhoa.gif')) {
            img.classList.add('img-gif');

        }
    });
    Array.from(h2Elements).forEach(h2 => {
        h2.classList.add('h2-custom')
    });
    Array.from(h3Elements).forEach(h3 => { h3.classList.add('h3-custom') })
}
checkImgMobile()
document.addEventListener('DOMContentLoaded', checkImgMobile); window.addEventListener('resize', checkImgMobile)

// Số lượng lớp ping cần tạo
const numberOfLayers = 10;
// Lấy phần tử chứa các lớp ping
const pingWrapper = document.querySelector('.ping-wrapper');
// Hàm tạo màu ngẫu nhiên tươi sáng
function getRandomBrightColor() {
    // Sử dụng HSL để tạo màu với độ bão hòa và độ sáng cao
    const hue = Math.floor(Math.random() * 360);
    const saturation = 50;
    const lightness = Math.floor(Math.random() * 21) + 20;
    return `hsl(${hue}, ${saturation}%, ${lightness}%)`;
}

// Tạo các lớp ping
for (let i = 1; i <= numberOfLayers; i++) {
    const pingLayer = document.createElement('div');
    pingLayer.className = `ping-layer ping-layer${i}`;
    pingLayer.style.width = `calc(100% + ${10 * i}px)`;
    pingLayer.style.height = `calc(100% + ${10 * i}px)`;
    pingLayer.style.border = '4px solid';
    pingLayer.style.borderColor = getRandomBrightColor();
    pingLayer.style.animationDelay = `${0.2 * (i - 0.6)}s`;
    pingWrapper.appendChild(pingLayer);
}